import React, { useState } from 'react';
import Sidebar from './DashboardComponents/Sidebar'; 
import HeaderBar from './DashboardComponents/HeaderBar';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function UserDetailPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [vehicleNumber, setVehicleNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/add_detail', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, vehicleNumber }),
      });
      if (!response.ok) {
        throw new Error('Failed to add vehicle');
      }
      // Handle success
      console.log('Vehicle details added successfully');
      setName('');
      setEmail('');
      setVehicleNumber('');
      setErrorMessage('');
    } catch (error) {
      console.error('Error adding detail of vehicle:', error);
    }
  };

  const handleChangeVehicleNumber = (e) => {
    const value = e.target.value;
    setVehicleNumber(value);
    if (value !== '') {
      if (!validateVehicleNumber(value)) {
        setErrorMessage('Invalid vehicle number. Please enter a valid vehicle number.');
      } else {
        setErrorMessage('');
      }
    } else {
      setErrorMessage('');
    }
  };

  const validateVehicleNumber = (vehicleNumber) => {
    return /^\d{4}[a-zA-Z]{1,3}\d{1,4}$/.test(vehicleNumber);
  };

  return (
    <div>
      <HeaderBar />
      <Sidebar />
      <Form style={{ margin: "10% 0% 0% 5%", width: "70%" }} onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formBasicName">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Enter Name" value={name} onChange={(e) => setName(e.target.value)} />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicVehicleNumber">
          <Form.Label>Vehicle Number</Form.Label>
          <Form.Control type="text" placeholder="Enter Vehicle Number" value={vehicleNumber} onChange={handleChangeVehicleNumber} />
          {errorMessage && <Form.Text className="text-danger">{errorMessage}</Form.Text>}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UserDetailPage;
